mapper.insert:
INSERT INTO `order` (
    order_no, user_id, status, total_amount, payment_amount, shipping_fee, discount_amount,
    payment_time, shipping_time, delivery_time, completion_time, cancellation_time,
    shipping_address_id, shipping_method, payment_method, note, create_time, update_time
) VALUES (
    #{orderNumber}, #{userId}, #{status}, #{totalAmount}, #{paymentAmount}, #{shippingFee}, #{discountAmount},
    #{payTime}, #{shipTime}, #{deliveryTime}, #{completeTime}, #{closeTime},
    #{addressId}, #{shippingMethod}, #{paymentMethod}, #{remark}, 
    #{createTime}, #{updateTime}
)

mapper.update:
UPDATE `order`
status = #{status}, total_amount = #{totalAmount}, payment_amount = #{paymentAmount}, shipping_fee = #{shippingFee}, discount_amount = #{discountAmount}, payment_time = #{payTime}, shipping_time = #{shipTime}, delivery_time = #{deliveryTime}, completion_time = #{completeTime}, cancellation_time = #{closeTime}, shipping_address_id = #{addressId}, shipping_id = #{logisticsId}, shipping_method = #{shippingMethod}, payment_method = #{paymentMethod}, note = #{remark}, update_time = NOW()
WHERE id = #{id}
